import React from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary
  }
}));

const FavLegend = props => {
  const classes = useStyles();

  return (
    // <Grid className={classes.root} item xs={3}>
    <Typography component="div">
      <div className="favLegendBox">
        <Box
          className="imageTitle"
          fontFamily="Arial"
          fontSize={32}
          m={1}
          style={{ display: "block", weight: "bold" }}
        >
          {props.title}
        </Box>
        <Box
          className="imageOwner"
          fontFamily="Arial"
          fontSize={16}
          m={2}
          style={{ display: "block" }}
        >
          Owner: {props.owner}
        </Box>
        <Box
          className="imageDate"
          fontFamily="Arial"
          fontSize={16}
          m={3}
          style={{ display: "block" }}
        >
          Date: {props.date}
        </Box>
        <Box
          className="stats"
          fontFamily="Arial"
          fontSize={16}
          m={4}
          style={{ display: "block" }}
        >
          <span>
            Views:{" "}
            <Box style={{ fontSize: 16, display: "inline" }}>
              {props.stats.views}
            </Box>{" "}
          </span>
          <span>
            Favorites:{" "}
            <Box style={{ fontSize: 16, display: "inline" }}>
              {props.stats.favorites}{" "}
            </Box>
          </span>
          <span>
            Comments:{" "}
            <Box style={{ fontSize: 16, display: "inline" }}>
              {props.stats.comments}{" "}
            </Box>
          </span>
        </Box>
      </div>
    </Typography>
  );
};

export default FavLegend;
