import React from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary
  }
}));

const FavLegend = props => {
  const classes = useStyles();

  return (
    // <Grid className={classes.root} item xs={3}>
    <Typography component="div">
      <div className="favLegendBox">
        <box
          className="imageTitle"
          fontFamily="Arial"
          fontSize="h2.fontSize"
          m={1}
          style={{ display: "block" }}
        >
          {props.title}
        </box>
        <box
          className="imageOwner"
          fontFamily="Arial"
          fontSize="h3.fontSize"
          m={2}
          style={{ display: "block" }}
        >
          Owner: {props.owner}
        </box>
        <box
          className="imageDate"
          fontFamily="Arial"
          fontSize="h4.fontSize"
          m={3}
          style={{ display: "block" }}
        >
          Date: {props.date}
        </box>
        <box
          className="stats"
          fontFamily="Arial"
          fontSize="h4.fontSize"
          m={4}
          style={{ display: "block" }}
        >
          <span>
            Views: <box fontSize="h2.fontSize">{props.stats.views}</box>{" "}
          </span>
          <span>
            Favorites:{" "}
            <box style={{ fontSize: "6rem" }}>{props.stats.favorites} </box>
          </span>
          <span>
            Comments: <box fontSize="2.fontSize">{props.stats.comments} </box>
          </span>
        </box>
      </div>
    </Typography>
    //   <Grid item xs={3}>
    //       <paper className="imageTitle">{props.title}</paper>
    //     </Grid>
    //     <Grid item xs={3}>
    //       <paper className="imageOwner">{props.owner}</paper>
    //     </Grid>
    //     <Grid item xs={3}>
    //       <paper className="imageDate">{props.date}</paper>
    //     </Grid>
    //     <Grid item xs={3}>
    //       <paper className="stats">
    //         <span>{props.stats.views}</span>
    //         <span>{props.stats.favorites}</span>
    //         <span>{props.stats.comments}</span>
    //       </paper>
    //     </Grid>
    // </div>
    // </Grid>
  );
};

export default FavLegend;
