import React from "react";
import { connect } from "react-redux";
import Image from "./Image";
import "./favorites.css";

const mapStateToProps = state => {
  return { favs: state.favs };
};

const Favorites = props => {
  return (
    // <ul className="recentImages">
    //   {props.favorites.map(favImage => {
    //     return <li key="{favImage.favImage_id}">{favImage.favImage_name}</li>;
    //   })}
    // </ul>

    <div className="favoriteBox">
      <div className="favoriteImages">
        {props.favs.map(el => (
          <div {...el} key={el.id}>
            <Image url={el.url} handleClick={() => {}} />
            <div class="imageTitle">{el.title}</div>
            <div class="imageOwner">{el.owner}</div>
            <div class="imageDate">{el.date}</div>
            <div class="stats">
              <span>{el.stats.views}</span>
              <span>{el.stats.favorites}</span>
              <span>{el.stats.comments}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default connect(mapStateToProps)(Favorites);
