import React from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Link from "@material-ui/core/Link";
import Grid from "@material-ui/core/Grid";
import SignInIcon from "../images/flckr.png";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import { connect } from "react-redux";

const useStyles = makeStyles(theme => ({
  "@global": {
    body: {
      backgroundColor: theme.palette.common.white
    }
  },
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  avatar: {
    margin: theme.spacing(1)
    // backgroundColor: theme.palette.secondary.main
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1)
  },
  submit: {
    margin: theme.spacing(3, 0, 2)
  }
}));

class SignIn extends React.Component {
  componentWillMount() {
    const { dispatch } = this.props;
    dispatch(
      actions.config({
        token: "/oauth/request_token",
        client_id: "0f434d4b-06bf-4cb2-b8f4-f20bf9349beb",
        client_secret: "530897d5880494a6a9ac92d1273d8ba5",
        url: "https://www.flickr.com/services",
        providers: {
          flickr: "/oauth/authorize"
        }
      })
    );
  }
  async handleSignin(e) {
    const { dispatch } = this.props;
    e.preventDefault();
    console.log(
      await dispatch(
        actions.signin(
          {
            username: this.refs.username.value,
            password: this.refs.password.value
          },
          console.log
        )
      ),
      "====="
    );
  }

  render() {
    const classes = useStyles();

    return (
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <div className={classes.paper}>
          <Avatar
            className={classes.avatar}
            alt="LOG IN to FLICKR"
            src={SignInIcon}
          />

          <Typography component="h1" variant="h5">
            Sign in to Flickr
          </Typography>
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              Sign In
            </Button>
            <Grid container>
              <Grid item xs>
                <Link href="#" variant="body2">
                  Forgot password?
                </Link>
              </Grid>
              <Grid item>
                <Link href="#" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
          </form>
        </div>
      </Container>
    );
  }
}
const mapStateToProps = state => {
  return { oauth: state.oauth };
};

export default connect(mapStateToProps)(SignIn);
