// src/js/actions/index.js
import {
  SELECT_PHOTO,
  FETCH_PHOTO_PENDING,
  FETCH_PHOTO_SUCCESS,
  FETCH_PHOTO_FAILURE
} from "../constants/action-types";

export function selectPhoto(payload) {
  console.log("a oto payload:", payload);
  return { type: SELECT_PHOTO, payload };
}

export function fetchPhotoPending() {
  return { type: FETCH_PHOTO_PENDING };
}

export function fetchPhotoSuccess() {
  return { type: FETCH_PHOTO_SUCCESS };
}

export function fetchPhotoFailure() {
  return { type: FETCH_PHOTO_FAILURE };
}
