// src/js/actions/index.js
import {
  SELECT_PHOTO,
  FETCH_PHOTO_PENDING,
  FETCH_PHOTO_SUCCESS,
  FETCH_PHOTO_FAILURE
} from "../constants/action-types";

export const selectPhoto = payload => ({
  type: SELECT_PHOTO,
  payload
});

export const fetchPhotoPending = () => {
  type: FETCH_PHOTO_PENDING;
};

export const fetchPhotoSuccess = data => {
  type: FETCH_PHOTO_SUCCESS, data;
};

export const fetchPhotoFailure = error => {
  type: FETCH_PHOTO_FAILURE, error;
};
